    <script>
        const form = '#searchF';
        const dataUrl = '{{ route('general.data') }}';

        $(document).on('change', '#search_major_ALL', function () {
            console.log('hi');
            // "전체" 체크박스 클릭 시 나머지 전부 체크/해제
            const isChecked = $(this).is(':checked');
            $("input[name='search_major[]']").not('#search_major_ALL').prop('checked', isChecked);
        });

        $(document).on('change', "input[name='search_major[]']", function () {
            if ($(this).attr('id') === 'search_major_ALL') return;

            const total = $("input[name='search_major[]']").not('#search_major_ALL').length;
            const checked = $("input[name='search_major[]']:checked").not('#search_major_ALL').length;

            $('#search_major_ALL').prop('checked', total === checked);
        });

        // 플래그 변수 추가로 무한루프 방지
        let isSearchAreaExecuting = false;

        $(document).on('change', '#si', function() {
            // search_area 함수 실행 중이면 이벤트 무시
            if (isSearchAreaExecuting) return;

            const _val = $(this).val();

            if (_val == "17") {
                $("#gu").val('').hide();
            } else {
                $("#gu").show();
            }

            // 구 옵션 동적 로드
            let ajaxData = {
                'case' : 'change-si',
                'si' : _val,
            };

            callbackAjax(dataUrl, ajaxData, function(data, error) {
                if (data && data.result['res'] != 'NOT') {
                    var items = data.result.items;
                    var $gu = $("[name='gu']");
                    $gu.empty();
                    $gu.append('<option value="">구/군</option>');
                    $gu.append('<option value="">전체</option>');

                    items.forEach(function(item) {
                        $gu.append('<option value="' + item.key + '">' + item.name + '</option>');
                    });
                } else if (data) {
                    // alert(data.result['msg']);
                }
            }, true);
        });

        function search_area(_si, _gu) {
            // 무한루프 방지 플래그 설정
            isSearchAreaExecuting = true;

            $("#si").val(_si);

            // 구 옵션 동적 로드
            let ajaxData = {
                'case' : 'change-si',
                'si' : _si,
            };

            callbackAjax(dataUrl, ajaxData, function(data, error) {
                if (data && data.result['res'] != 'NOT') {
                    var $gu = $("[name='gu']");
                    $gu.empty();
                    $gu.append('<option value="">구/군</option>');
                    $gu.append('<option value="">전체</option>');

                    if (_si == "17") {
                        $gu.val('').hide();
                    } else {
                        $gu.show();
                        var items = data.result.items;
                        items.forEach(function(item) {
                            $gu.append('<option value="' + item.key + '">' + item.name + '</option>');
                        });
                        $gu.val(_gu); // 옵션 세팅 후 값 변경
                    }

                    // 옵션, 값 모두 반영된 후에만 검색
                    search_post();

                } else if (data) {
                    alert(data.result['msg']);
                }

                // AJAX 완료 후 플래그 해제
                isSearchAreaExecuting = false;
            }, true);


        }

        //더보기
        $(document).on('click', '.btn-more', function() {
            let ajaxData = newFormData(form);
            ajaxData.append('case', 'btn-more');

            callMultiAjax(dataUrl, ajaxData);
        });

        $(document).on('click', '#search_post', function() {
            search_post();
        });

        function search_post() {
            let ajaxData = newFormData(form);

            callMultiAjax(dataUrl, ajaxData);
        }

    </script>

